@media (max-width: 800px) {
  .footer-nav-tabs {
    font: 700 22px $f_main;
    padding: 20px;
  }

  .footer-info {
    padding: 20px;
  }

  .footer-tip {
    padding: 20px;
  }
}

@media (max-width: 600px) {
  .footer-tip__icon {
    margin-right: 0;
  }

  .footer-tip__desc {
    display: none;
  }
}

