commits.content:
.header-callback__content {
  max-width: 500px;

  &._disabled {
    display: none;
  }
}

.header-callback-form__input,
.header-callback-form__textarea {
  max-width: 100%;
  min-width: 100%;
  max-height: 100%;
  min-height: 100%;
}

.header-callback-form__submit {
  max-width: 100%;
  min-width: 100%;
  margin: 60px 0 40px;
}

.header-callback-form-policy__link {
  color: $c_blue;
}

.header-callback-success,
.header-callback-fail {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;

  &._disabled {
    display: none;
  }
}

.header-callback-success__img,
.header-callback-fail__img {
  object-fit: contain;
  object-position: center;
  max-width: 80px;
  min-width: 80px;
  max-height: 80px;
  min-height: 80px;
  margin-bottom: 20px;
}

.header-callback-success__ttl,
.header-callback-fail__ttl {
  margin-bottom: 10px;
  font: 500 30px $f_main;
}

.header-callback__ttl.qs_section-ttl {
  font-weight: 700;
}
.header-callback__vacancy.qs_desc {
  width: 100%;
  font-weight: 500;
  font-size: 23px;
  line-height: 27px;
  color: #949fa8;
}

.header-callback__load-file {
  font-weight: 500;
  font-size: 14px;
  line-height: 16px;
  color: #176abd;
  display: inline-flex;
  align-items: center;
  margin-top: 25px;
}
.header-callback__load-file::before {
  content: ' ';
  background-image: url(../assets/icons/paper_clip.svg);
  background-repeat: no-repeat;
  background-position: center;
  height: 20px;
  width: 20px;
  margin-right: 10px;
}

@media (max-height: 800px) {
  .header-callback {
    padding-top: 20px;
    padding-bottom: 20px;
  }
}
