===================================================================
--- 
+++ 
@@ -0,0 +1,193 @@
+.page-home {
+  @include bg-settings();
+  background-image: url('../assets/imgs/home-bg_opt.jpg');
+}
+
+.page-home-bg__video {
+  position: fixed;
+  z-index: -10;
+  top: 0;
+  left: 0;
+  object-fit: cover;
+  object-position: center;
+  width: 100%;
+  height: 100%;
+}
+
+.page-home-bg__slogan {
+  position: absolute;
+  z-index: -10;
+  left: 80px;
+  bottom: 160px;
+  width: calc(100% - 160px);
+  max-width: 750px;
+  margin-bottom: 40px;
+  opacity: 1;
+  font: 900 65px $f_main;
+  color: $c_white;
+  transition: opacity 400ms ease;
+
+  &._disabled {
+    opacity: 0;
+    transition: opacity 400ms ease;
+  }
+}
+
+.page-home .content-wrapper {
+  // display: grid;
+  // grid-template-columns: 1fr;
+  // grid-template-rows: 1fr max-content;
+  display: block;
+  position: relative;
+  overflow: hidden;
+}
+
+.page-home .main {
+  //align-self: end;
+  // display: flex;
+  // flex-direction: column;
+  // justify-content: flex-end;
+  height: 100%;
+  max-height: 100%;
+  opacity: 0;
+  overflow: auto;
+  //overflow: hidden;
+  transition: opacity 400ms ease;
+
+  &._show {
+    opacity: 1;
+    transition: opacity 400ms ease;
+  }
+}
+
+.page-home .main section {
+  //position: absolute;
+  //bottom: 0;
+  //left: 0;
+  //display: none;
+  width: 100%;
+  min-height: calc(100vh - 91px);
+  // height: 100vh; /* Use vh as a fallback for browsers that do not support Custom Properties */
+  // height: calc(var(--vh, 1vh) * 100);
+  padding: 0;
+  //background-color: $c_white;
+  //overflow-y: auto;
+
+  &:first-of-type {
+    height: 100%;
+    background-color: transparent;
+  }
+
+  // &:nth-of-type(2n) {
+  //   background-color: $c_gray-light2;
+
+  //   // & .qs_section {
+  //   //   background-color: $c_gray-light2;
+  //   // }
+  // }
+
+  &._opened {
+    z-index: 10;
+    display: block;
+  }
+
+  &._current {
+    z-index: 20;
+    display: block;
+  }
+
+  & .qs_section {
+    //height: 100%;
+    padding: 80px;
+    background-color: $c_white;
+    min-height: inherit;
+  }
+
+  &:nth-of-type(2n + 1) {
+    & .qs_section {
+      background-color: $c_gray-light2;
+    }
+  }
+
+  & .qs_desc {
+    color: $c_gray;
+  }
+}
+
+.page-home section .qs_section-ttl {
+  font: 900 30px $f_main;
+}
+
+.page-home .aside {
+  background-color: transparent;
+}
+
+// .page-home .footer-local .footer-info {
+//   display: none;
+// }
+
+.page-home .footer {
+  position: absolute;
+  bottom: 0;
+  width: 100%;
+}
+
+// .page-home .footer-nav-tabs {
+//   display: none;
+// }
+
+.page-home .footer .footer-info {
+  display: none;
+}
+
+.page-home .footer-tip._return svg {
+  display: none;
+  transform: rotate(-90deg);
+}
+
+@media (max-width: 900px) {
+  .page-home-bg__slogan {
+    left: 40px;
+    width: calc(100% - 80px);
+    font: 900 45px $f_main;
+  }
+
+  .page-home .main section .qs_section {
+    padding: 40px;
+  }
+}
+
+@media (max-height: 900px) {
+  .page-home-bg__slogan {
+    left: 40px;
+    width: calc(100% - 80px);
+    font: 900 45px $f_main;
+  }
+}
+
+@media (max-width: 600px) {
+  .page-home-bg__slogan {
+    left: 20px;
+    width: calc(100% - 40px);
+    font: 900 30px $f_main;
+  }
+}
+
+@media (max-height: 600px) {
+  .page-home-bg__slogan {
+    left: 20px;
+    width: calc(100% - 40px);
+    font: 900 30px $f_main;
+  }
+}
+
+@media (max-width: 500px) {
+  .page-home .main section .qs_section {
+    padding: 20px;
+  }
+}
+
+// скрываем каптчу
+.grecaptcha-badge {
+  display: none !important;
+}
\ No newline at end of file

