  // }

  &._opened {
    z-index: 10;
    display: block;
  }

  &._current {
    z-index: 20;
    display: block;
  }

  & .qs_section {
    //height: 100%;
    padding: 80px;
    background-color: $c_white;
    min-height: inherit;
